create table biz_financial_category
(
    id          bigint                  not null comment '主键ID'
        primary key,
    name        varchar(255)            null comment '分类名称',
    code        varchar(32)             null comment '分类编码',
    icon        varchar(255)            null comment '分类图标',
    color       varchar(255)            null comment '分类颜色',
    sort        int                     null comment '分类排序',
    remark      varchar(255)            null comment '分类备注',
    create_id   bigint                  null comment '创建人',
    create_time datetime                null comment '创建时间',
    update_id   bigint                  null comment '修改人',
    update_time datetime                null comment '修改时间',
    del_flag    char        default '0' null comment '逻辑删除',
    parent_code varchar(32) default '0' null comment '父级分类char (0表示一级分类）'
)
    comment '财务分类表' row_format = DYNAMIC;

