-- ----------------------------
-- Table structure for biz_focus_goal
-- ----------------------------
DROP TABLE IF EXISTS `biz_focus_goal`;
CREATE TABLE `biz_focus_goal` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `user_id` bigint NOT NULL COMMENT '用户ID',
  `title` varchar(255) NOT NULL COMMENT '目标标题',
  `description` text COMMENT '目标描述',
  `category_id` bigint DEFAULT NULL COMMENT '分类ID (逻辑关联，非外键约束)',
  `start_date` datetime NOT NULL COMMENT '开始日期',
  `end_date` datetime NOT NULL COMMENT '结束日期',
  `status` char(20) NOT NULL DEFAULT 'active' COMMENT '目标状态 (draft: 草稿, active: 进行中, completed: 已完成, archived: 已归档)',
  `final_progress` int NOT NULL DEFAULT '0' COMMENT '最终进度百分比 (0-100)',
  `completion_status` char(30) DEFAULT NULL COMMENT '完成状态 (early: 提前完成, on_time: 按时完成, delayed_completed: 延期完成, delayed_incomplete: 延期未完成)',
  `has_delayed_tasks` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否存在延期任务',
  `expected_duration_sec` int DEFAULT NULL COMMENT '预期持续时间（秒）',
  `actual_duration_sec` int DEFAULT NULL COMMENT '实际持续时间（秒）',
  `overdue_completion_time_sec` int DEFAULT NULL COMMENT '逾期完成时间（秒）',
  `create_id` bigint DEFAULT NULL COMMENT '创建人',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_id` bigint DEFAULT NULL COMMENT '修改人',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `del_flag` char(1) DEFAULT '0' COMMENT '逻辑删除',
  PRIMARY KEY (`id`),
  KEY `idx_completion_status` (`completion_status`) COMMENT '完成状态索引',
  KEY `idx_user_end_date` (`user_id`,`end_date`) COMMENT '用户ID和结束日期联合索引',
  KEY `idx_user_status` (`user_id`,`status`) COMMENT '用户ID和状态联合索引'
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='专注目标表';

