  `priority` char(10) DEFAULT 'medium' COMMENT '优先级 (high: 高, medium: 中, low: 低)',
  `expected_duration_sec` int DEFAULT NULL COMMENT '预期持续时间（秒）',
  `actual_consumed_sec` int DEFAULT NULL COMMENT '实际消耗时间（秒）',
  `create_id` bigint DEFAULT NULL COMMENT '创建人',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_id` bigint DEFAULT NULL COMMENT '修改人',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `del_flag` char(1) DEFAULT '0' COMMENT '逻辑删除',
  PRIMARY KEY (`id`),
  KEY `idx_goal_id` (`goal_id`) COMMENT '目标ID索引',
  KEY `idx_status` (`status`) COMMENT '任务状态索引',
  KEY `idx_user_plan_date` (`user_id`,`plan_start_date`,`plan_end_date`) COMMENT '用户ID和计划日期范围联合索引'
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='任务表';

