-- ----------------------------
-- Table structure for sys_menu
-- ----------------------------
DROP TABLE IF EXISTS `sys_menu`;
CREATE TABLE `sys_menu` (
  `id` bigint NOT NULL COMMENT '主键',
  `parent_id` varchar(60) DEFAULT NULL COMMENT '父级菜单id',
  `label` varchar(30) DEFAULT NULL COMMENT '菜单名称',
  `title` varchar(30) DEFAULT NULL COMMENT '鼠标悬浮展示内容',
  `menu_type` varchar(20) DEFAULT NULL COMMENT '菜单/页面/按钮/外链',
  `router_path` varchar(100) DEFAULT NULL COMMENT '路由地址',
  `component_path` varchar(100) DEFAULT NULL COMMENT '组建路径',
  `visible` char(1) DEFAULT NULL COMMENT '是否显示（0显示、1隐藏）',
  `status` char(1) DEFAULT NULL COMMENT '菜单状态(0正常、1停用)',
  `perms` varchar(50) DEFAULT NULL COMMENT '权限标识符',
  `icon` varchar(100) DEFAULT NULL COMMENT '菜单图标',
  `sort` int DEFAULT NULL COMMENT '排序',
  `create_id` bigint DEFAULT NULL COMMENT '创建人id',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_id` varchar(60) DEFAULT NULL COMMENT '最后一次更新人id',
  `update_time` datetime DEFAULT NULL COMMENT '最后一次更新时间',
  `del_flag` char(1) DEFAULT NULL COMMENT '逻辑删除标志',
  `remark` varchar(500) DEFAULT NULL COMMENT '备注',
  `cache` char(1) DEFAULT NULL COMMENT '是否缓存页面（0 缓存、1不缓存）',
  `link_path` varchar(300) DEFAULT NULL COMMENT '外链类型页面地址',
  `query` varchar(500) DEFAULT NULL COMMENT '路由携带的参数',
  `view_tab` char(1) DEFAULT NULL COMMENT '允许view-tab显示标签',
  `link_open_type` char(10) DEFAULT NULL COMMENT '链接打开方式',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci ROW_FORMAT=DYNAMIC COMMENT='系统菜单权限表';

