create table sys_login_log
(
    id             bigint       not null comment '主键'
        primary key,
    description    varchar(100) null comment '业务描述',
    type_code      varchar(50)  null comment '业务类型编码',
    type_msg       varchar(100) null comment '业务类型描述',
    class_name     varchar(200) null comment '类名',
    method_name    varchar(200) null comment '方法名',
    ip_address     varchar(20)  null comment 'ip地址',
    create_name    varchar(60)  null comment '创建人昵称',
    username       varchar(60)  null comment '用户名',
    create_id      bigint       null comment '创建人id（操作人）',
    create_time    datetime     null comment '创建时间（操作时间）',
    execute_time   int          null comment '执行时长ms',
    params         varchar(500) null comment '参数',
    result         text         null comment '返回值',
    error_msg      varchar(500) null comment '异常信息',
    error_stack    text         null comment '堆栈信息',
    url            varchar(500) null comment '请求url',
    user_agent     varchar(500) null comment '用户代理字符串，包含客户端操作系统、浏览器内核等信息',
    del_flag       char         null comment '逻辑删除',
    execute_status char         null comment '日志执行状态',
    cache_key      varchar(200) null comment '用户缓存key'
)
    comment '系统登录日志' row_format = DYNAMIC;

create index cache_key
    on sys_login_log (cache_key);

