create table sys_menu
(
    id             bigint       not null comment '主键'
        primary key,
    parent_id      varchar(60)  null comment '父级菜单id',
    label          varchar(30)  null comment '菜单名称',
    title          varchar(30)  null comment '鼠标悬浮展示内容',
    menu_type      varchar(20)  null comment '菜单/页面/按钮/外链',
    router_path    varchar(100) null comment '路由地址',
    component_path varchar(100) null comment '组建路径',
    visible        char         null comment '是否显示（0显示、1隐藏）',
    status         char         null comment '菜单状态(0正常、1停用)',
    perms          varchar(50)  null comment '权限标识符',
    icon           varchar(100) null comment '菜单图标',
    sort           int          null comment '排序',
    create_id      bigint       null comment '创建人id',
    create_time    datetime     null comment '创建时间',
    update_id      varchar(60)  null comment '最后一次更新人id',
    update_time    datetime     null comment '最后一次更新时间',
    del_flag       char         null comment '逻辑删除标志',
    remark         varchar(500) null comment '备注',
    cache          char         null comment '是否缓存页面（0 缓存、1不缓存）',
    link_path      varchar(300) null comment '外链类型页面地址',
    query          varchar(500) null comment '路由携带的参数',
    view_tab       char         null comment '允许view-tab显示标签',
    link_open_type char(10)     null comment '链接打开方式'
)
    comment '系统菜单权限表' row_format = DYNAMIC;

